import Icon from '@material-ui/core/Icon'
import Button from '@material-ui/core/Button'
import { useState } from 'react'
const icons = {
  'todo': 'assignment',
  'in-progress': 'autorenew',
  'done': 'assignment_turned_in',
  'deleted': 'delete',
  'canceled': 'cancel'
}
const colors = {
  'todo': 'grey',
  'in-progress': 'blue',
  'done': 'green',
  'deleted': 'red',
  'canceled': 'black'
}
export default ({ children, status }) => {
  const [itemStatus, updateStatus] = useState('todo')
  return (
    <>
      <li className="task-list__item">
        <Icon style={{ color: colors[status], margin: 'auto 4px auto 0px' }}>{icons[status]}</Icon>
        <p className="title">{children}</p>
        <div className="btn-group">
          {/* { status === 'todo' && (<Button start />
          <Button cancel/>
          )} */}
          {/* <Button type="button" onClick={(e)=>{updateStatus('in-progress')}} color="primary">Start</Button>
          <Button type="button" onClick={(e)=>{updateStatus('done')}} color="secondary">Done</Button>
          <Button type="button" onClick={(e)=>{updateStatus('canceled')}}>Cancel</Button>
          <Button type="button" onClick={(e)=>{updateStatus('deleted')}}>Delete</Button> */}
        </div>
      </li>
      <style jsx>{
        `
        .task-list__item {
          padding: 4px 8px;
          margin: 2px 0px;
          display: flex;
          background: #fff;
        }
        .title {
          flex: 1;
          margin: auto 0;
        }
        .btn-group {
          display: flex;
        }
        `
      }</style>
    </>
  )
}
