import Head from 'next/head'
import { useState } from 'react'
import TaskList from 'components/TaskList'
import TaskListItem from 'components/TaskListItem'
import TaskInput from 'components/TaskInput'
export default function Home() {
  const [tasks, setTasks] = useState([])
  const updateTask = ({ id, title, status }) => {
    const editTask = {
      id,
      title,
      status,
    }
    console.log('edit task ', editTask);
    const newTasks = tasks.map((item, index) => {
      console.log('item ', item);
      return item.id === editTask.id ? Object.assign({}, item, editTask) : Object.assign({}, item)
    });
    console.log('newTasks ', newTasks)
    setTasks(newTasks)
  }
  return (
    <div className='container'>
      <Head>
        <title>Todo Task Web App</title>
        <link rel='icon' href='/favicon.ico' />
        <link
          href='https://fonts.googleapis.com/css?family=Roboto:300,400,500,700,400italic|Roboto+Mono:400,500|Material+Icons'
          rel='stylesheet'
        />
      </Head>
      <main style={{ maxWidth: '966px', margin: 'auto' }}>
        <TaskInput onAdd={(task) => setTasks([...tasks, task])} />
        <TaskList>
          {tasks.map((task) => {
            return (
              <TaskListItem
                key={task.id}
                id={task.id}
                status={task.status}
                updateTask={task => updateTask(task)}>
                {task.title}
              </TaskListItem>
            )
          })}
        </TaskList>
      </main>
    </div >
  )
}
